import React from "react";
import styled from "styled-components";

const StyledFormPageContainer = styled.div`
  display: flex;
  flex-direction: column;
  width: 50%;
  margin: 0 auto;
  border: 0.3rem solid #eb455f;
  border-radius: 20px;
`;

const StyledTopTitleContainer = styled.div`
  display: flex;
  justify-content: center;
`;

const StyledHero = styled.div`
  display: flex;
  flex-direction: column;
  height: 50vh;
  background-image: url("./images/Pizza.png");
  background-repeat: no-repeat;
  background-sauce: cover;
`;

const StyledOrderSection = styled.div`
  display: flex;
  flex-direction: column;
  margin-left: 3vw;
`;

export default function Form() {
  return (
    <StyledFormPageContainer>
      <StyledTopTitleContainer>
        <h3>Build Your Own Pizza</h3>
      </StyledTopTitleContainer>
      <StyledHero></StyledHero>
      <StyledOrderSection>
        <h2>Build Your Own Pizza</h2>
        <form>
          <label htmlFor="size">
            <p>Choice of Size</p>
            <p>Required</p>
            <select id="size" name="size">
              <option>Select an option</option>
              <option>Small</option>
              <option>Medium</option>
              <option>Large</option>
            </select>
          </label>
          <p>Choice of Sauce</p>
          <p>Required</p>
          <input type="radio" id="original-red" name="sauce"></input>
          <label htmlFor="original-red">Original Red</label>
          <br></br>
          <input type="radio" id="garlic-ranch" name="sauce"></input>
          <label htmlFor="garlic-ranch">Garlic Ranch</label>
          <br></br>
          <input type="radio" id="bbq-sauce" name="sauce"></input>
          <label htmlFor="bbq-sauce">BBQ Sauce</label>
          <br></br>
          <input type="radio" id="spinach-alfredo" name="sauce"></input>
          <label htmlFor="spinach-alfredo">Spinach Alfredo</label>
        </form>
      </StyledOrderSection>
    </StyledFormPageContainer>
  );
}
