import * as Yup from "yup";
export const orderFormSchema = Yup.object().shape({
  sizeDropdown: Yup.string().required(),
  sauce: Yup.string().required(),
  specialText: Yup.string()
    .required("you must fill this section")
    .min(5, "must be more than 5"),
});
