import React from "react";
import Form from "./Form";
export default function Basket(props) {
  const { formData, toppings } = props;

  return (
    <div>
      <div>
        <h2>Complete Your Order</h2>
      </div>
      <div>
        <form>
          <label htmlFor="name">
            Full Name:
            <input type="text" id="name" name="name" />
          </label>
          <label htmlFor="address">
            Address:
            <input type="text" id="address" name="address" />
          </label>
        </form>
        <div>
          <p>Size: {formData.sizeDropdown}</p>
          <p>Sauce: {formData.sauce}</p>
          <p>Toppings:</p>
          <ul>
            {Object.entries(toppings).map(([key, value]) => (
              <li>
                {key.toLocaleUpperCase()} :{" "}
                {toppings[key] === true ? "yes" : "no"}
              </li>
            ))}
          </ul>
        </div>
        {formData.glutenFreeCrust ? <p>Gluten Free Crust: Yes</p> : null}
        {formData.specialText ? (
          <p>Special Instructions: {formData.specialText}</p>
        ) : (
          "No special instructions"
        )}
      </div>
    </div>
  );
}
