import React from "react";
import styled from "styled-components";

const StyledBasketContainer = styled.div`
  display: flex;
  flex-direction: column;
  width: 50%;
  margin: 0 auto;
  border: 0.3rem solid #eb455f;
  border-radius: 20px;
  margin-bottom: 2vh;
`;

const StyledTopTitleContainer = styled.div`
  display: flex;
  justify-content: center;
`;

const StyledPersonalDetailsForm = styled.form`
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
  row-gap: 1vh;
`;

export default function Basket(props) {
  const { formData, toppings } = props;

  return (
    <StyledBasketContainer>
      <StyledTopTitleContainer>
        <h2>Complete Your Order</h2>
      </StyledTopTitleContainer>
      <div>
        <StyledPersonalDetailsForm>
          <label htmlFor="name">
            Full Name:
            <input type="text" id="name" name="name" />
          </label>
          <label htmlFor="address">
            Address:
            <input type="text" id="address" name="address" />
          </label>
        </StyledPersonalDetailsForm>
        <div>
          <p>Size: {formData.sizeDropdown}</p>
          <p>Sauce: {formData.sauce}</p>
          <p>Toppings:</p>
          <ul>
            {Object.entries(toppings).map(([key, value]) => (
              <li>
                {key.toLocaleUpperCase()} :{" "}
                {toppings[key] === true ? "Yes" : "No"}
              </li>
            ))}
          </ul>
        </div>
        {formData.glutenFreeCrust ? <p>Gluten Free Crust: Yes</p> : null}

        <p>
          Special Instructions:{" "}
          {formData.specialText ? formData.specialText : "None"}
        </p>
        <p>Order Quantity: {formData.orderQuantity}</p>
      </div>
      <div>
        <p>Total: $12.00</p>
        <form>
          <label>
            Place Order
            <input type="submit" />
          </label>
        </form>
      </div>
    </StyledBasketContainer>
  );
}
