import React, { useState, useEffect } from "react";
import { Route, Switch } from "react-router-dom";
import * as Yup from "yup";
import { orderFormSchema } from "./validations/OrderFormValidation";
import Header from "./Components/Header";
import Mainpage from "./Components/Mainpage";
import Form from "./Components/Form";
import Basket from "./Components/Basket";
import Footer from "./Components/Footer";

const App = () => {
  // states
  const [formData, setFormData] = useState({
    sizeDropdown: "",
    sauce: "",
    glutenFreeCrust: "",
    specialText: "",
    orderQuantity: "1",
  });

  const [orderButtonDisabled, setOrderButtonDisabled] = useState(true);

  const [toppings, setToppings] = useState({
    pepperoni: false,
    sausage: false,
    canadianBacon: false,
    spicyItalianSausage: false,
    grilledChicken: false,
    onions: false,
    greenPepper: false,
    dicedTomatos: false,
    blackOlives: false,
    roastedGarlic: false,
    artichokeHearts: false,
    threeCheese: false,
    pineapple: false,
    extraCheese: false,
  });

  const [orderFormErrors, setOrderFormErrors] = useState({
    sizeDropdown: "",
    sauce: "",
    specialText: "",
    orderQuantity: "",
  });
  // handle changes
  const handleChange = (event) => {
    const { name, value, type, checked } = event.target;
    let newValue = value;
    if (type === "checkbox") {
      newValue = checked;
    }
    checkOrderFormErrors(name, newValue);
    setFormData({ ...formData, [name]: newValue });
  };

  const handleTopping = (event) => {
    const { name, value, type, checked } = event.target;
    let newValue = value;
    if (type === "checkbox") {
      newValue = checked;
    }
    setToppings({ ...toppings, [name]: newValue });
  };

  useEffect(() => {
    orderFormSchema
      .isValid(formData)
      .then((result) => setOrderButtonDisabled(!result));
  }, [formData]);

  // validation errors
  function checkOrderFormErrors(name, value) {
    Yup.reach(orderFormSchema, name)
      .validate(value)
      .then(() => {
        setOrderFormErrors({
          ...orderFormErrors,
          [name]: "",
        });
      })
      .catch((err) => {
        setOrderFormErrors({
          ...orderFormErrors,
          [name]: err.errors[0],
        });
      });
  }
  // UI
  return (
    <>
      <Header />
      <Switch>
        <Route exact path="/">
          <Mainpage />
        </Route>
        <Route path="/pizza">
          <Form
            handleChange={handleChange}
            formData={formData}
            orderButtonDisabled={orderButtonDisabled}
            toppings={toppings}
            handleTopping={handleTopping}
            orderFormErrors={orderFormErrors}
          />
        </Route>
        <Route path="/basket">
          <Basket formData={formData} toppings={toppings} />
        </Route>
      </Switch>
      <Footer />
    </>
  );
};
export default App;
