import React from "react";
import styled from "styled-components";

const StyledBasketContainer = styled.div`
  display: flex;
  flex-direction: column;
  width: 50%;
  margin: 0 auto;
  border: 0.3rem solid #eb455f;
  border-radius: 20px;
  margin-bottom: 15vh;
  align-items: center;
`;

const StyledTopTitleContainer = styled.div`
  display: flex;
  justify-content: center;
`;

const StyledOrderStatusContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  column-gap: 2vh;
  margin-bottom: 4vh;
`;

const StyledOrderStatusIndicator = styled.p`
  padding: 0.5rem;
  background-color: #03c988;
  color: white;
  font-weight: bold;
  border-radius: 20px;
`;

const StyledOrderDetailsContainer = styled.div`
  display: flex;
  flex-direction: column;
`;

const StyledOrderDetailsHeadings = styled.p`
  font-weight: bold;
`;
const StyledConfirmationSection = styled.div`
  display: flex;
  justify-content: space-evenly;
  align-items: center;
  border-top: 0.1rem solid black;
  height: 15vh;
  width: 100%;
  background-color: #ffeeee;
  border-radius: 20px;
  margin-top: 5vh;
  font-size: 1.2rem;
`;

export default function Basket(props) {
  const { formData, toppings } = props;

  return (
    <StyledBasketContainer>
      <StyledTopTitleContainer>
        <h2>Order Details</h2>
      </StyledTopTitleContainer>
      <div>
        <StyledOrderStatusContainer>
          {" "}
          <h4>Order status: </h4>
          <StyledOrderStatusIndicator>Preparing</StyledOrderStatusIndicator>
        </StyledOrderStatusContainer>

        <StyledOrderDetailsContainer>
          <StyledOrderDetailsHeadings>Size:</StyledOrderDetailsHeadings>
          <p>{formData.sizeDropdown}</p>
          <StyledOrderDetailsHeadings></StyledOrderDetailsHeadings>
          <p> Sauce: {formData.sauce}</p>
          <StyledOrderDetailsHeadings>Toppings:</StyledOrderDetailsHeadings>
          <ul>
            {Object.entries(toppings).map(([key, value]) => (
              <li>
                {key.toLocaleUpperCase()} :{" "}
                {toppings[key] === true ? "Yes" : "No"}
              </li>
            ))}
          </ul>
        </StyledOrderDetailsContainer>
        {formData.glutenFreeCrust ? (
          <div>
            <StyledOrderDetailsHeadings>
              Gluten Free Crust:
            </StyledOrderDetailsHeadings>
            <p>Yes</p>
          </div>
        ) : null}

        <StyledOrderDetailsHeadings>
          Special Instructions:{" "}
        </StyledOrderDetailsHeadings>
        <p> {formData.specialText ? formData.specialText : "None"}</p>
      </div>
      <StyledConfirmationSection>
        <StyledOrderDetailsHeadings>
          Order Quantity: {formData.orderQuantity}
        </StyledOrderDetailsHeadings>
        <StyledOrderDetailsHeadings>Total: $12.00</StyledOrderDetailsHeadings>
      </StyledConfirmationSection>
    </StyledBasketContainer>
  );
}
