import React from "react";
import Form from "./Form";
export default function Basket(props) {
  const { formData } = props;

  return (
    <div>
      <div>
        <h2>Complete Your Order</h2>
      </div>
      <div>
        <form>
          <label htmlFor="name">
            Full Name:
            <input type="text" id="name" name="name" />
          </label>
          <label htmlFor="address">
            Address:
            <input type="text" id="address" name="address" />
          </label>
        </form>
        <div>
          <p>Size: {formData.sizeDropdown}</p>
          <p>Sauce: {formData.sauce}</p>
          <p>Toppings:</p>
          <ul>
            {Object.keys(formData.toppings).map((topping) => (
              <li>
                {topping}: {formData.toppings.topping}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}
