import React, { useState, useEffect } from "react";
import { Route, Switch } from "react-router-dom";
import * as Yup from "yup";
import axios from "axios";
import { orderFormSchema } from "./validations/OrderFormValidation";
import { toppingsFormSchema } from "./validations/ToppingsValidation";
import Header from "./Components/Header";
import Mainpage from "./Components/Mainpage";
import Form from "./Components/Form";
import Basket from "./Components/Basket";
import Footer from "./Components/Footer";

const App = () => {
  // states
  const [formData, setFormData] = useState({
    sizeDropdown: "",
    sauce: "",
    glutenFreeCrust: "",
    specialText: "",
    name: "",
    address: "",
    orderQuantity: "1",
  });
  const [adminOrderDetails, setAdminOrderDetails] = useState("");

  const [orderButtonDisabled, setOrderButtonDisabled] = useState(true);

  const [toppings, setToppings] = useState({
    pepperoni: false,
    sausage: false,
    canadianBacon: false,
    spicyItalianSausage: false,
    grilledChicken: false,
    onions: false,
    greenPepper: false,
    dicedTomatos: false,
    blackOlives: false,
    roastedGarlic: false,
    artichokeHearts: false,
    threeCheese: false,
    pineapple: false,
    extraCheese: false,
  });

  const [orderFormErrors, setOrderFormErrors] = useState({
    sizeDropdown: "",
    sauce: "",
    glutenFreeCrust: "",
    specialText: "",
    name: "",
    address: "",
    orderQuantity: null,
  });

  const [toppingsFormErrors, setToppingsFormErrors] = useState({
    pepperoni: "",
    sausage: "",
    canadianBacon: "",
    spicyItalianSausage: "",
    grilledChicken: "",
    onions: "",
    greenPepper: "",
    dicedTomatos: "",
    blackOlives: "",
    roastedGarlic: "",
    artichokeHearts: "",
    threeCheese: "",
    pineapple: "",
    extraCheese: "",
  });
  const [orderStatus, setOrderStatus] = useState(false);

  const [sum, setSum] = useState(12);

  const [extraSum, setExtraSum] = useState(null);

  // handle changes
  const handleChange = (event) => {
    const { name, value, type, checked } = event.target;
    let newValue = value;

    if (type === "checkbox") {
      newValue = checked;
    }

    checkOrderFormErrors(name, newValue);

    setFormData({ ...formData, [name]: newValue });
  };

  const [toppingArray, setToppingArray] = useState([]);
  console.log(toppingArray);

  const handleTopping = (event) => {
    const { name, value, type, checked } = event.target;
    let newValue = value;
    if (type === "checkbox") {
      newValue = checked;
    }
    checkToppingsFormErrors(name, newValue);
    setToppings({ ...toppings, [name]: newValue });

    for (let i = 0; i < Object.keys(toppings).length; i++) {
      if (
        toppings[Object.keys(toppings)[i]] === true &&
        !toppingArray.includes(toppings[Object.keys(toppings)[i]])
      ) {
        setToppingArray([...toppingArray, Object.keys(toppings)[i]]);
      }
    }
  };
  const handleOrderQuantitySum = (event) => {
    const { value } = event.target;
    setSum(value * 12);
  };

  const handleOrderCrustSum = (event) => {
    if (event.target.checked === true) {
      setExtraSum(1);
    } else if (event.target.checked !== true) {
      setExtraSum(0);
    }
  };

  useEffect(() => {
    orderFormSchema
      .isValid(formData)
      .then((result) => setOrderButtonDisabled(!result));
  }, [formData]);

  // submit
  const handleSubmit = (event) => {
    event.preventDefault();
    axios
      .post("https://reqres.in/api/users", formData)
      .then((response) => {
        setAdminOrderDetails(response.data);
        setOrderStatus(true);
        setFormData({
          sizeDropdown: "",
          sauce: "",
          glutenFreeCrust: "",
          specialText: "",
          name: "",
          address: "",
          orderQuantity: null,
        });
      })
      .catch((error) => {
        console.log(error);
      });
  };

  // validation errors
  function checkOrderFormErrors(name, value) {
    Yup.reach(orderFormSchema, name)
      .validate(value)
      .then(() => {
        setOrderFormErrors({
          ...orderFormErrors,
          [name]: "",
        });
      })
      .catch((err) => {
        setOrderFormErrors({
          ...orderFormErrors,
          [name]: err.errors[0],
        });
      });
  }

  function checkToppingsFormErrors(name, value) {
    Yup.reach(toppingsFormSchema, name)
      .validate(value)
      .then(() => {
        setToppingsFormErrors({
          ...toppingsFormErrors,
          [name]: "",
        });
      })
      .catch((err) => {
        setToppingsFormErrors({
          ...toppingsFormErrors,
          [name]: err.errors[0],
        });
      });
  }
  // UI
  return (
    <>
      <Header />
      <Switch>
        <Route exact path="/">
          <Mainpage />
        </Route>
        <Route path="/pizza">
          <Form
            handleChange={handleChange}
            formData={formData}
            orderButtonDisabled={orderButtonDisabled}
            toppings={toppings}
            handleTopping={handleTopping}
            orderFormErrors={orderFormErrors}
            handleSubmit={handleSubmit}
            adminOrderDetails={adminOrderDetails}
            sum={sum}
            extraSum={extraSum}
            handleOrderQuantitySum={handleOrderQuantitySum}
            handleOrderCrustSum={handleOrderCrustSum}
          />
        </Route>
        <Route path="/basket">
          <Basket
            toppings={toppings}
            adminOrderDetails={adminOrderDetails}
            orderStatus={orderStatus}
            sum={sum}
            extraSum={extraSum}
          />
        </Route>
      </Switch>
      <p>{toppingsFormErrors.pepperoni}</p>
      <Footer />
    </>
  );
};
export default App;
