const App = () => {
  const [formData, setFormData] = useState({
    sizeDropdown: "",
  });

  const handleChange = (event) => {
    console.log(event.target);
  };
  return (
    <>
      <Header />
      <Switch>
        <Route exact path="/">
          <Mainpage />
        </Route>
        <Route path="/pizza">
          <Form handleChange={handleChange} />
        </Route>
      </Switch>
      <Footer />
    </>
  );
};
export default App;
