import React from "react";
import Form from "./Form";
export default function Basket(props) {
  const { formData } = props;
  let toppicsArray = [];
  Object.keys(formData.toppings).map((key) => {
    if (formData.toppings.key === "true") {
      toppicsArray.push(key);
    }
  });

  console.log(toppicsArray);
  return (
    <div>
      <div>
        <h2>Complete Your Order</h2>
      </div>
      <div>
        <form>
          <label htmlFor="name">
            Full Name:
            <input type="text" id="name" name="name" />
          </label>
          <label htmlFor="address">
            Address:
            <input type="text" id="address" name="address" />
          </label>
        </form>
        <div>
          <p>Size: {formData.sizeDropdown}</p>
          <p>Sauce: {formData.sauce}</p>
          <p>Toppings:</p>
          <ul>
            {toppicsArray.map((topping) => (
              <li>{topping}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}
