import React from "react";
import styled from "styled-components";

const StyledBasketContainer = styled.div`
  display: flex;
  flex-direction: column;
  width: 50%;
  margin: 0 auto;
  border: 0.3rem solid #eb455f;
  border-radius: 20px;
  margin-bottom: 2vh;
  align-items: center;
`;

const StyledTopTitleContainer = styled.div`
  display: flex;
  justify-content: center;
`;

const StyledPersonalDetailsForm = styled.form`
  display: flex;
  flex-direction: column;
  row-gap: 1vh;
`;

const StyledPersonalDetailsFormAddress = styled.input`
  height: 10vh;
`;

const StyledConfirmationSection = styled.div`
  display: flex;
  justify-content: space-evenly;
  align-items: center;
  border-top: 0.1rem solid black;
  height: 10vh;
  width: 100%;
  background-color: #ffeeee;
  border-radius: 20px;
  margin-top: 5vh;
`;

const StyledPlaceOrderButton = styled.button`
  height: 5vh;
  padding: 2vh;
`;

export default function Basket(props) {
  const { formData, toppings } = props;

  return (
    <StyledBasketContainer>
      <StyledTopTitleContainer>
        <h2>Complete Your Order</h2>
      </StyledTopTitleContainer>
      <div>
        <h4>Personal Details</h4>
        <StyledPersonalDetailsForm>
          <label htmlFor="name">
            Full Name: <br></br>
            <input type="text" id="name" name="name" />
          </label>
          <label htmlFor="address">
            Address:<br></br>
            <StyledPersonalDetailsFormAddress
              type="text"
              id="address"
              name="address"
            />
          </label>
        </StyledPersonalDetailsForm>
        <h4>Order Details</h4>
        <div>
          <p>Size: {formData.sizeDropdown}</p>
          <p>Sauce: {formData.sauce}</p>
          <p>Toppings:</p>
          <ul>
            {Object.entries(toppings).map(([key, value]) => (
              <li>
                {key.toLocaleUpperCase()} :{" "}
                {toppings[key] === true ? "Yes" : "No"}
              </li>
            ))}
          </ul>
        </div>
        {formData.glutenFreeCrust ? <p>Gluten Free Crust: Yes</p> : null}

        <p>
          Special Instructions:{" "}
          {formData.specialText ? formData.specialText : "None"}
        </p>
      </div>
      <StyledConfirmationSection>
        <p>Order Quantity: {formData.orderQuantity}</p>
        <p>Total: $12.00</p>
        <form>
          <label>
            <StyledPlaceOrderButton disabled="true">
              Place Order
            </StyledPlaceOrderButton>
          </label>
        </form>
      </StyledConfirmationSection>
    </StyledBasketContainer>
  );
}
