import * as Yup from "yup";
export const orderFormSchema = Yup.object().shape({
  sizeDropdown: Yup.string()
    .required("you must fill this section")
    .min(1, "you must fill this section"),
  sauce: Yup.string().required(),
  glutenFreeCrust: Yup.string(),
  specialText: Yup.string().max(
    140,
    "Your message must be less than 140 characters."
  ),
  orderQuantity: Yup.number("Min. order quantity is 1.").min(
    1,
    "Min. order quantity is 1."
  ),
});
