import React from "react";
import styled from "styled-components";

const StyledMainPageContainer = styled.div`
  display: flex;
  flex-direction: column;
`;

const StyledHero = styled.div`
  display: flex;
  flex-direction: column;
  text-align: center;
  align-items: center;
  align-content: center;
  justify-content: center;
  row-gap: 5vh;
  border: 0.1rem solid black;
  height: 50vh;
`;

const StyledHeroTitle = styled.h1`
  font-weight: bolder;
`;

const StyledHeroButton = styled.button`
  background-color: white;
  color: black;
  border: 0.1rem solid black;
  padding: 0.5vw 4vw;
  &:hover {
    background-color: black;
    color: white;
  }
`;

const StyledAllRestaurantsContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  width: 90%;
  margin: 5vh auto;
`;

const StyledRestaurantContainer = styled.div`
  display: flex;
  flex-direction: column;
  border: 0.1rem solid black;
  width: 30%;
`;

const StyledRestaurantImage = styled.img`
  width: 100%;
`;

const StyledRestaurantTagsContainer = styled.div`
  display: flex;
  column-gap: 2vw;
`;

const StyledRestaurantTags = styled.p`
  background-color: white;
  color: black;
  border: 0.1rem solid black;
  padding: 0.1vw 0.6vw;
  font-size: 0.8rem;
  &:hover {
    background-color: black;
    color: white;
  }
`;
export default function Mainpage() {
  return (
    <StyledMainPageContainer>
      <StyledHero>
        <StyledHeroTitle>
          Your favourite food, delivered while coding
        </StyledHeroTitle>
        <StyledHeroButton>Pizza?</StyledHeroButton>
      </StyledHero>
      <h4>Food Delivery in Gotham City</h4>
      <StyledAllRestaurantsContainer>
        <StyledRestaurantContainer>
          <div>
            <div>
              <StyledRestaurantImage
                src="./images/pizza.png"
                alt="placeholder image"
              />
              <h5>McDonald's</h5>
              <p>$ - American - Fast Food - Burgers</p>
              <StyledRestaurantTagsContainer>
                <StyledRestaurantTags>20-30 Min</StyledRestaurantTags>
                <StyledRestaurantTags>$5.99 Delivery Fee</StyledRestaurantTags>
              </StyledRestaurantTagsContainer>
            </div>
          </div>
        </StyledRestaurantContainer>
      </StyledAllRestaurantsContainer>
    </StyledMainPageContainer>
  );
}
