import React from "react";
import styled from "styled-components";

const StyledFormPageContainer = styled.div`
  display: flex;
  flex-direction: column;
  width: 50%;
  margin: 0 auto;
  border: 0.3rem solid #eb455f;
  border-radius: 20px;
  margin-bottom: 2vh;
`;

const StyledTopTitleContainer = styled.div`
  display: flex;
  justify-content: center;
`;

const StyledHero = styled.div`
  display: flex;
  flex-direction: column;
  height: 50vh;
  background-image: url("./images/Pizza.png");
  background-repeat: no-repeat;
  background-size: cover;
`;

const StyledOrderSection = styled.div`
  display: flex;
  flex-direction: column;
`;

const StyledOrderSectionHeading = styled.div`
  background-color: #ffeeee;
  padding: 2vh 0;
  margin: 2vh 0;
`;

const StyledOrderSectionHeadingTitle = styled.p`
  font-weight: bold;
`;

const StyledOrderSectionHeadingSubTitle = styled.p`
  font-size: 0.8rem;
`;

const StyledToppingsContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: space-around;
`;

const StyledToppingsContainerSelection = styled.div`
  width: 40%;
`;

const StyledSpecialInstructionsContainer = styled.label`
  display: flex;
  justify-content: center;
`;
const StyledSpecialInstructionsInput = styled.input`
  width: 90%;
  height: 7vh;
  margin: 3vh 0;
`;

const StyledPlaceOrderContainer = styled.label`
  display: flex;
  justify-content: space-evenly;
  border-top: 0.1rem solid black;
  height: 10vh;
`;

const StyledPlaceOrderInput = styled.input`
  width: 20%;
`;

const StyledPlaceOrderButton = styled.button`
  width: 70%;
  display: flex;
  justify-content: space-between;
  font-size: 1.3rem;
  align-items: center;
  margin: 2vh 0;
`;

export default function Form(props) {
  const { handleChange, formData, orderButtonDisabled } = props;

  return (
    <StyledFormPageContainer>
      <StyledTopTitleContainer>
        <h3>Build Your Own Pizza</h3>
      </StyledTopTitleContainer>
      <StyledHero></StyledHero>
      <StyledOrderSection>
        <h2>Build Your Own Pizza</h2>

        <form id="pizza-form">
          <label htmlFor="size">
            <StyledOrderSectionHeading>
              <StyledOrderSectionHeadingTitle>
                Choice of Size
              </StyledOrderSectionHeadingTitle>
              <StyledOrderSectionHeadingSubTitle>
                Required
              </StyledOrderSectionHeadingSubTitle>
            </StyledOrderSectionHeading>
            <select
              id="size-dropdown"
              name="sizeDropdown"
              value={formData.sizeDropdown}
              onChange={handleChange}
            >
              <option disabled selected>
                Select an option
              </option>
              <option>Small</option>
              <option>Medium</option>
              <option>Large</option>
            </select>
          </label>

          <StyledOrderSectionHeading>
            <StyledOrderSectionHeadingTitle>
              Choice of Sauce
            </StyledOrderSectionHeadingTitle>
            <StyledOrderSectionHeadingSubTitle>
              Required
            </StyledOrderSectionHeadingSubTitle>
          </StyledOrderSectionHeading>
          <input
            type="radio"
            id="original-red"
            name="sauce"
            onChange={handleChange}
            value="original-red"
          ></input>
          <label htmlFor="original-red">Original Red</label>
          <br></br>
          <input
            type="radio"
            id="garlic-ranch"
            name="sauce"
            onChange={handleChange}
            value="garlic-ranch"
          ></input>
          <label htmlFor="garlic-ranch">Garlic Ranch</label>
          <br></br>
          <input
            type="radio"
            id="bbq-sauce"
            name="sauce"
            onChange={handleChange}
            value="bbq-sauce"
          ></input>
          <label htmlFor="bbq-sauce">BBQ Sauce</label>
          <br></br>
          <input
            type="radio"
            id="spinach-alfredo"
            name="sauce"
            onChange={handleChange}
            value="spinach-alfredo"
          ></input>
          <label htmlFor="spinach-alfredo">Spinach Alfredo</label>
          <StyledOrderSectionHeading>
            <StyledOrderSectionHeadingTitle>
              Add Toppings
            </StyledOrderSectionHeadingTitle>
            <StyledOrderSectionHeadingSubTitle>
              Choose up to 10
            </StyledOrderSectionHeadingSubTitle>
          </StyledOrderSectionHeading>
          <StyledToppingsContainer>
            <StyledToppingsContainerSelection>
              <input
                type="checkbox"
                id="pepperoni"
                name="pepperoni"
                onChange={handleChange}
                checked={formData.pepperoni}
              />
              <label>Pepperoni</label>
            </StyledToppingsContainerSelection>
            <StyledToppingsContainerSelection>
              <input
                type="checkbox"
                id="sausage"
                name="sausage"
                onChange={handleChange}
                checked={formData.sausage}
              />
              <label>Sausage</label>
            </StyledToppingsContainerSelection>
            <StyledToppingsContainerSelection>
              <input
                type="checkbox"
                id="canadian-bacon"
                name="canadianBacon"
                onChange={handleChange}
                checked={formData.canadianBacon}
              />
              <label>Canadian Bacon</label>
            </StyledToppingsContainerSelection>
            <StyledToppingsContainerSelection>
              <input
                type="checkbox"
                id="spicy-italian-sausage"
                name="spicyItalianSausage"
                onChange={handleChange}
                checked={formData.spicyItalianSausage}
              />
              <label>Spicy Italian Sausage</label>
            </StyledToppingsContainerSelection>
            <StyledToppingsContainerSelection>
              <input
                type="checkbox"
                id="grilled-chicken"
                name="grilledChicken"
                onChange={handleChange}
                checked={formData.grilledChicken}
              />
              <label>Grilled Chicken</label>
            </StyledToppingsContainerSelection>
            <StyledToppingsContainerSelection>
              <input
                type="checkbox"
                id="onions"
                name="onions"
                onChange={handleChange}
                checked={formData.onions}
              />
              <label>Onions</label>
            </StyledToppingsContainerSelection>
            <StyledToppingsContainerSelection>
              <input
                type="checkbox"
                id="green-pepper"
                name="greenPepper"
                onChange={handleChange}
                checked={formData.greenPepper}
              />
              <label>Green Pepper</label>
            </StyledToppingsContainerSelection>
            <StyledToppingsContainerSelection>
              <input
                type="checkbox"
                id="diced-tomatos"
                name="dicedTomatos"
                onChange={handleChange}
                checked={formData.dicedTomatos}
              />
              <label>Diced Tomatos</label>
            </StyledToppingsContainerSelection>
            <StyledToppingsContainerSelection>
              <input
                type="checkbox"
                id="black-olives"
                name="blackOlives"
                onChange={handleChange}
                checked={formData.blackOlives}
              />
              <label>Black Olives</label>
            </StyledToppingsContainerSelection>
            <StyledToppingsContainerSelection>
              <input
                type="checkbox"
                id="roasted-garlic"
                name="roastedGarlic"
                onChange={handleChange}
                checked={formData.roastedGarlic}
              />
              <label>Roasted Garlic</label>
            </StyledToppingsContainerSelection>
            <StyledToppingsContainerSelection>
              <input
                type="checkbox"
                id="artichoke-hearts"
                name="artichokeHearts"
                onChange={handleChange}
                checked={formData.artichokeHearts}
              />
              <label>Artichoke Hearts</label>
            </StyledToppingsContainerSelection>
            <StyledToppingsContainerSelection>
              <input
                type="checkbox"
                id="three-cheese"
                name="threeCheese"
                onChange={handleChange}
                checked={formData.threeCheese}
              />
              <label>Three Cheese</label>
            </StyledToppingsContainerSelection>
            <StyledToppingsContainerSelection>
              <input
                type="checkbox"
                id="pineapple"
                name="pineapple"
                onChange={handleChange}
                checked={formData.pineapple}
              />
              <label>Pineapple</label>
            </StyledToppingsContainerSelection>
            <StyledToppingsContainerSelection>
              <input
                type="checkbox"
                id="extra-cheese"
                name="extraCheese"
                onChange={handleChange}
                checked={formData.extraCheese}
              />
              <label>Extra Cheese</label>
            </StyledToppingsContainerSelection>
          </StyledToppingsContainer>

          <StyledOrderSectionHeading>
            <StyledOrderSectionHeadingTitle>
              Choice of Substitute
            </StyledOrderSectionHeadingTitle>
            <StyledOrderSectionHeadingSubTitle>
              Choose up to 1
            </StyledOrderSectionHeadingSubTitle>
          </StyledOrderSectionHeading>
          <input
            type="checkbox"
            id="gluten-free-crust"
            name="glutenFreeCrust"
            checked={formData.glutenFreeCrust}
            onChange={handleChange}
          />
          <label htmlFor="gluten-free-crust">Gluten Free Crust (+ $100) </label>

          <StyledOrderSectionHeading>
            <StyledOrderSectionHeadingTitle>
              Special Instructions
            </StyledOrderSectionHeadingTitle>
          </StyledOrderSectionHeading>
          <StyledSpecialInstructionsContainer>
            <StyledSpecialInstructionsInput
              type="text"
              placeholder="Anything else you'd like to add?"
              id="special-text"
              name="specialText"
              value={formData.specialText}
              onChange={handleChange}
            />
          </StyledSpecialInstructionsContainer>
          <StyledPlaceOrderContainer>
            <StyledPlaceOrderInput
              type="number"
              id="order-quantity"
              name="orderQuantity"
              value={formData.orderQuantity}
              onChange={handleChange}
            />
            <StyledPlaceOrderButton
              id="order-button"
              disabled={orderButtonDisabled}
            >
              <p>Add to Order</p> <p>$17.99</p>
            </StyledPlaceOrderButton>
          </StyledPlaceOrderContainer>
        </form>
      </StyledOrderSection>
    </StyledFormPageContainer>
  );
}
