import * as Yup from "yup";
export const orderFormSchema = Yup.object().shape({
  sizeDropdown: Yup.boolean().oneOf([true], "You must choose a size option"),
  sauce: Yup.boolean().oneOf([true], "You must choose a sauce option"),
});
