import React from "react";
import styled from "styled-components";

const StyledBasketContainer = styled.div`
  display: flex;
  flex-direction: column;
  width: 50%;
  margin: 0 auto;
  border: 0.3rem solid #eb455f;
  border-radius: 20px;
  margin-bottom: 15vh;
  align-items: center;
`;

const StyledTopTitleContainer = styled.div`
  display: flex;
  justify-content: center;
`;

const StyledOrderStatusContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  column-gap: 2vw;
  margin-bottom: 4vh;
`;

const StyledOrderStatusIndicator = styled.p`
  padding: 0.5rem;
  background-color: #03c988;
  color: white;
  font-weight: bold;
  border-radius: 20px;
`;

const StyledOrderDetailsContainer = styled.div`
  display: flex;
  flex-direction: column;
`;

const StyledOrderDetailsHeadings = styled.p`
  font-weight: bold;
`;

const StyledOrderSection = styled.div`
  display: flex;
  column-gap: 2vw;
`;

const StyledMainOrderDetailsContainer = styled.div`
  display: flex;
  flex-direction: column;
  row-gap: 2vh;
  padding-bottom: 5vh;
`;

export default function Basket(props) {
  const { formData, toppings } = props;

  return (
    <StyledBasketContainer>
      <StyledTopTitleContainer>
        <h2>Order Details</h2>
      </StyledTopTitleContainer>
      <StyledMainOrderDetailsContainer>
        <StyledOrderStatusContainer>
          {" "}
          <h4>Order status: </h4>
          <StyledOrderStatusIndicator>Preparing</StyledOrderStatusIndicator>
        </StyledOrderStatusContainer>

        <StyledOrderDetailsContainer>
          <StyledOrderSection>
            <StyledOrderDetailsHeadings>Size:</StyledOrderDetailsHeadings>
            <p>{formData.sizeDropdown}</p>
          </StyledOrderSection>
          <StyledOrderSection>
            <StyledOrderDetailsHeadings>Sauce: </StyledOrderDetailsHeadings>
            <p> {formData.sauce}</p>
          </StyledOrderSection>
          <StyledOrderDetailsHeadings>Toppings:</StyledOrderDetailsHeadings>
          <ul>
            {Object.entries(toppings).map(([key, value]) => (
              <li>
                {key.toLocaleUpperCase()} :{" "}
                {toppings[key] === true ? "Yes" : "No"}
              </li>
            ))}
          </ul>
        </StyledOrderDetailsContainer>
        {formData.glutenFreeCrust ? (
          <StyledOrderSection>
            <StyledOrderDetailsHeadings>
              Gluten Free Crust:
            </StyledOrderDetailsHeadings>
            <p>Yes</p>
          </StyledOrderSection>
        ) : null}
        <StyledOrderSection>
          <StyledOrderDetailsHeadings>
            Special Instructions:{" "}
          </StyledOrderDetailsHeadings>
          <p> {formData.specialText ? formData.specialText : "None"}</p>
        </StyledOrderSection>
        <StyledOrderSection>
          <StyledOrderDetailsHeadings>
            Order Quantity:
          </StyledOrderDetailsHeadings>
          <p> {formData.orderQuantity}</p>
        </StyledOrderSection>
        <StyledOrderSection>
          <StyledOrderDetailsHeadings>Total: </StyledOrderDetailsHeadings>
          <p>$12.00</p>
        </StyledOrderSection>
      </StyledMainOrderDetailsContainer>
    </StyledBasketContainer>
  );
}
