import React from "react";
import styled from "styled-components";

const StyledMainPageContainer = styled.div`
  display: flex;
  flex-direction: column;
`;

const StyledHero = styled.div`
  display: flex;
  flex-direction: column;
  text-align: center;
  align-items: center;
  align-content: center;
  justify-content: center;
  row-gap: 5vh;
  border: 0.1rem solid black;
  height: 50vh;
`;

const StyledHeroTitle = styled.h1`
  font-weight: bolder;
`;

const StyledHeroButton = styled.button`
  background-color: white;
  color: black;
  border: 0.1rem solid black;
  padding: 0.5vw 4vw;
  &:hover {
    background-color: black;
    color: white;
  }
`;

const StyledAllRestaurantsContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  width: 90%;
  margin: 5vh auto;
`;

const StyledRestaurantContainer = styled.div`
  display: flex;
  flex-direction: column;
  border: 0.1rem solid black;
  width: 30%;
`;
export default function Mainpage() {
  return (
    <StyledMainPageContainer>
      <StyledHero>
        <StyledHeroTitle>
          Your favourite food, delivered while coding
        </StyledHeroTitle>
        <StyledHeroButton>Pizza?</StyledHeroButton>
      </StyledHero>
      <h4>Food Delivery in Gotham City</h4>
      <StyledAllRestaurantsContainer>
        <StyledRestaurantContainer>
          <div>
            <div>
              <img src="/Pizza.jpg"></img>
              <h5>McDonald's</h5>
              <p>$ - American - Fast Food - Burgers</p>
              <div>
                <p>20-30 Min</p>
                <p>$5.99 Delivery Fee</p>
              </div>
            </div>
          </div>
        </StyledRestaurantContainer>
      </StyledAllRestaurantsContainer>
    </StyledMainPageContainer>
  );
}
